#' Update metadata
#'
#' Update FinBIF dataset metadata at GBIF.
#'
#' @param metadata List. FinBIF dataset metadata generated by `get_metadata`.
#' @param uuid Character. GBIF dataset identifier.
#' @param created Character. Time dataset created in ISO8601 format.
#' @param url Character. URL of GBIF API. Defaults to system environment
#'   variable, "GBIF_API".
#' @param user Character. GBIF username. Defaults to system environment
#'   variable, "GBIF_USER".
#' @param pass Character. GBIF password. Defaults to system environment
#'   variable, "GBIF_PASS".
#' @return A list.
#' @examples \dontrun{
#'
#' m <- get_metadata("HR.3991")
#' send_gbif_dataset_metadata(m)
#'
#' }
#' @importFrom httr authenticate content RETRY status_code
#' @importFrom jsonlite fromJSON
#' @export

update_gbif_dataset_metadata <- function(
  metadata,
  uuid,
  created,
  url = Sys.getenv("GBIF_API"),
  user = Sys.getenv("GBIF_USER"),
  pass = Sys.getenv("GBIF_PASS")
) {

  auth <- httr::authenticate(user, pass)

  metadata <- c(
    list(
      key      = uuid,
      created  = created,
      modified = format(as.POSIXlt(Sys.time(), "Etc/UTC"), "%FT%R:%OS+00:00")
    ),
    metadata
  )

  res <- httr::RETRY(
    "PUT",
    url = url,
    config = auth,
    path = sprintf("v1/dataset/%s", uuid),
    body = metadata,
    encode = "json"
  )

  status <- httr::status_code(res)

  ok <- identical(status, 204L)

  stopifnot("Update failed. Could not send metadata to GBIF" = ok)

  invisible(NULL)

}
